// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "./IERC20.sol";
import "./LPERC20.sol";

/**
 * @title LiquidityPool
 * @dev Main liquidity pool contract for Project Aegis
 */
contract LiquidityPool {
    // Immutable variables
    address public immutable factoryOwner;
    string public regionName; // Cannot be immutable as it's a reference type
    
    // Token contracts
    IERC20 public stakingToken;
    LPERC20 public lpToken;
    
    // Pool state
    uint256 public totalLiquidityStaked;
    PoolStatusContract public status;
    uint256 public rewardsPot;
    uint256 public apyBasisPoints = 500; // 5.00% default APY
    
    // Constants
    uint256 public constant COLLATERAL_PERCENTAGE_BP = 2000; // 20%
    uint256 public constant UNSTAKE_TIME_LOCK_DURATION = 24 hours;
    uint256 public constant BASIS_POINTS = 10000;
    
    // Mappings
    mapping(address => Stake) public stakers;
    mapping(address => Debt[]) public userDebts;
    mapping(address => uint256) public debtRelatedUnstakeTimelock;
    
    // Fixed: Changed from mappings to uint256 for total tracking
    uint256 public totalInterPoolLoans; // Total amount this pool owes to other pools
    uint256 public totalInterPoolLent; // Total amount this pool has lent to other pools
    
    // Additional mapping to track specific pool loans if needed for detailed tracking
    mapping(address => uint256) public interPoolLoansByPool; // Amount borrowed from each specific pool
    mapping(address => uint256) public interPoolLentToPool; // Amount lent to each specific pool
    
    // Structs
    struct Stake {
        uint256 stakedAmount;
        uint256 collateralAmount;
        uint256 lpTokensMinted;
        uint256 stakeTimestamp;
    }
    
    struct Debt {
        address user;
        address merchantAddress;
        uint256 amount;
        uint256 timestamp;
        bool isRepaid;
    }
    
    // New struct for pool registry interface
    struct PoolInfo {
        address poolAddress;
        uint256 availableLiquidity;
        uint256 userCollateral;
    }
    
    // Enums
    enum PoolStatusContract { ACTIVE, PAUSED, INACTIVE }
    
    // Events
    event Staked(address indexed user, uint256 amountStaked, uint256 lpTokensReceived);
    event Unstaked(address indexed user, uint256 amountUnstaked, uint256 lpTokensBurned);
    event FallbackPaymentMade(address indexed user, address indexed merchant, uint256 amount, address poolUsed);
    event DebtRepaid(address indexed user, uint256 amountRepaid);
    event RewardsDistributed(uint256 totalRewardsDistributed, uint256 timestamp);
    event PoolStatusChanged(PoolStatusContract newStatus);
    event ApyUpdated(uint256 newApyBasisPoints);
    event RewardsAdded(uint256 amount);
    
    // New events for inter-pool functionality
    event InterPoolLoanMade(address indexed fromPool, address indexed toPool, uint256 amount);
    event InterPoolLoanRepaid(address indexed fromPool, address indexed toPool, uint256 amount);
    event MultiPoolFallbackPayment(address indexed user, address indexed merchant, uint256 amount, uint256 poolsUsed);
    
    // Modifiers
    modifier onlyFactoryOwner() {
        require(msg.sender == factoryOwner, "LiquidityPool: caller is not the factory owner");
        _;
    }
    
    modifier whenActive() {
        require(status == PoolStatusContract.ACTIVE, "LiquidityPool: pool is not active");
        _;
    }
    
    // New modifier for inter-pool operations
    modifier onlyRegisteredPool() {
        require(IPoolRegistry(factoryOwner).isRegisteredPool(msg.sender), "LiquidityPool: caller is not a registered pool");
        _;
    }
    
    constructor(
        string memory _regionName,
        address _factoryOwner,
        address _stakingTokenAddress
    ) {
        regionName = _regionName;
        factoryOwner = _factoryOwner;
        stakingToken = IERC20(_stakingTokenAddress);
        status = PoolStatusContract.ACTIVE;
        
        // Deploy LP token
        lpToken = new LPERC20(
            string(abi.encodePacked("AegisLP-", _regionName)),
            string(abi.encodePacked("ALP-", _regionName)),
            address(this)
        );
    }
    
    // View Functions
    function getPoolStatus() external view returns (PoolStatusContract) {
        return status;
    }
    
    function totalLiquidity() external view returns (uint256) {
        return totalLiquidityStaked;
    }
    
    function apy() external view returns (uint256) {
        return apyBasisPoints;
    }
    
    function lpTokenSupply() external view returns (uint256) {
        return lpToken.totalSupply();
    }
    
    function getStake(address _user) external view returns (Stake memory) {
        return stakers[_user];
    }
    
    function getUserDebts(address _user) external view returns (Debt[] memory) {
        return userDebts[_user];
    }
    
    function getCollateralAvailable(address _user) external view returns (uint256) {
        uint256 totalDebt = 0;
        Debt[] memory debts = userDebts[_user];
        
        for (uint256 i = 0; i < debts.length; i++) {
            if (!debts[i].isRepaid) {
                totalDebt += debts[i].amount;
            }
        }
        
        return stakers[_user].collateralAmount > totalDebt ? 
               stakers[_user].collateralAmount - totalDebt : 0;
    }
    
    function getActiveDebtAmount(address _user) external view returns (uint256) {
        uint256 totalDebt = 0;
        Debt[] memory debts = userDebts[_user];
        
        for (uint256 i = 0; i < debts.length; i++) {
            if (!debts[i].isRepaid) {
                totalDebt += debts[i].amount;
            }
        }
        
        return totalDebt;
    }
    
    // Fixed: Now uses totalInterPoolLoans instead of mapping
    function getAvailableLiquidity() external view returns (uint256) {
        uint256 balance = stakingToken.balanceOf(address(this));
        return balance > totalInterPoolLoans ? balance - totalInterPoolLoans : 0;
    }
    
    function getTotalCollateralAcrossPools(address _user) external view returns (uint256) {
        address[] memory allPools = IPoolRegistry(factoryOwner).getAllPools();
        uint256 totalCollateral = 0;
        
        for (uint256 i = 0; i < allPools.length; i++) {
            if (allPools[i] != address(0)) {
                totalCollateral += LiquidityPool(allPools[i]).getCollateralAvailable(_user);
            }
        }
        
        return totalCollateral;
    }
    
    // Admin Functions
    function toggleStatus() external onlyFactoryOwner {
        if (status == PoolStatusContract.ACTIVE) {
            status = PoolStatusContract.PAUSED;
        } else if (status == PoolStatusContract.PAUSED) {
            status = PoolStatusContract.ACTIVE;
        }
        // INACTIVE state can only be set explicitly
        
        emit PoolStatusChanged(status);
    }
    
    function setStatus(PoolStatusContract _newStatus) external onlyFactoryOwner {
        status = _newStatus;
        emit PoolStatusChanged(status);
    }
    
    function setApy(uint256 _newApyBasisPoints) external onlyFactoryOwner {
        require(_newApyBasisPoints <= 5000, "LiquidityPool: APY cannot exceed 50%");
        apyBasisPoints = _newApyBasisPoints;
        emit ApyUpdated(_newApyBasisPoints);
    }
    
    function addRewardsToPot(uint256 amount) external onlyFactoryOwner {
        require(amount > 0, "LiquidityPool: amount must be greater than 0");
        rewardsPot += amount;
        emit RewardsAdded(amount);
    }
    
    // Core User Functions
    function stake(uint256 _amount) external whenActive {
        require(_amount > 0, "LiquidityPool: amount must be greater than 0");
        require(
            stakingToken.transferFrom(msg.sender, address(this), _amount),
            "LiquidityPool: token transfer failed"
        );
        
        // Calculate collateral amount (20% of staked amount)
        uint256 collateralAmount = (_amount * COLLATERAL_PERCENTAGE_BP) / BASIS_POINTS;
        
        // Calculate LP tokens to mint (1:1 ratio for simplicity)
        uint256 lpTokensToMint;
        if (lpToken.totalSupply() == 0) {
            lpTokensToMint = _amount;
        } else {
            lpTokensToMint = (_amount * lpToken.totalSupply()) / totalLiquidityStaked;
        }
        
        // Update staker data
        Stake storage userStake = stakers[msg.sender];
        userStake.stakedAmount += _amount;
        userStake.collateralAmount += collateralAmount;
        userStake.lpTokensMinted += lpTokensToMint;
        userStake.stakeTimestamp = block.timestamp;
        
        // Update pool state
        totalLiquidityStaked += _amount;
        
        // Mint LP tokens
        lpToken.mint(msg.sender, lpTokensToMint);
        
        emit Staked(msg.sender, _amount, lpTokensToMint);
    }
    
     function unstake(uint256 _lpTokenAmount) external whenActive {
        require(_lpTokenAmount > 0, "LiquidityPool: LP token amount must be greater than 0");
        
        Stake storage userStake = stakers[msg.sender];
        require(userStake.lpTokensMinted >= _lpTokenAmount, "LiquidityPool: insufficient LP tokens");
        
        // Check timelock
        require(
            block.timestamp >= debtRelatedUnstakeTimelock[msg.sender],
            "LiquidityPool: unstake timelock active"
        );
        
        // Calculate proportion being unstaked
        uint256 proportionBasisPoints = (_lpTokenAmount * BASIS_POINTS) / userStake.lpTokensMinted;
        
        // Calculate amounts to return
        uint256 stakedAmountToReturn = (userStake.stakedAmount * proportionBasisPoints) / BASIS_POINTS;
        uint256 collateralToReduce = (userStake.collateralAmount * proportionBasisPoints) / BASIS_POINTS;
        
        // Check if remaining collateral covers active debts
        uint256 remainingCollateral = userStake.collateralAmount - collateralToReduce;
        uint256 activeDebt = this.getActiveDebtAmount(msg.sender);
        require(
            remainingCollateral >= activeDebt,
            "LiquidityPool: insufficient collateral for active debts"
        );
        
        // Update staker data
        userStake.stakedAmount -= stakedAmountToReturn;
        userStake.collateralAmount -= collateralToReduce;
        userStake.lpTokensMinted -= _lpTokenAmount;
        
        // Update pool state
        totalLiquidityStaked -= stakedAmountToReturn;
        
        // Burn LP tokens
        lpToken.burn(msg.sender, _lpTokenAmount);
        
        // Transfer staked tokens back to user
        require(
            stakingToken.transfer(msg.sender, stakedAmountToReturn),
            "LiquidityPool: token transfer failed"
        );
        
        emit Unstaked(msg.sender, stakedAmountToReturn, _lpTokenAmount);
    }
    
    function fallbackPay(address _merchantAddress, uint256 _amount) external whenActive {
        require(_amount > 0, "LiquidityPool: amount must be greater than 0");
        require(_merchantAddress != address(0), "LiquidityPool: invalid merchant address");
        
        // Check if single pool has sufficient liquidity AND user has sufficient collateral
        uint256 userCollateralInThisPool = this.getCollateralAvailable(msg.sender);
        uint256 availableLiquidityInThisPool = this.getAvailableLiquidity();
        
        if (userCollateralInThisPool >= _amount && availableLiquidityInThisPool >= _amount) {
            // Standard single-pool fallback payment
            _executeSinglePoolPayment(msg.sender, _merchantAddress, _amount);
        } else {
            // Multi-pool fallback payment required
            uint256 totalUserCollateral = this.getTotalCollateralAcrossPools(msg.sender);
            require(totalUserCollateral >= _amount, "LiquidityPool: insufficient total collateral across all pools");
            
            _executeMultiPoolPayment(msg.sender, _merchantAddress, _amount);
        }
    }
    
    function _executeSinglePoolPayment(address _user, address _merchantAddress, uint256 _amount) internal {
        // Record debt
        userDebts[_user].push(Debt({
            user: _user,
            merchantAddress: _merchantAddress,
            amount: _amount,
            timestamp: block.timestamp,
            isRepaid: false
        }));
        
        // Set unstake timelock
        uint256 newTimelock = block.timestamp + UNSTAKE_TIME_LOCK_DURATION;
        if (newTimelock > debtRelatedUnstakeTimelock[_user]) {
            debtRelatedUnstakeTimelock[_user] = newTimelock;
        }
        
        // Transfer funds to merchant
        require(
            stakingToken.transfer(_merchantAddress, _amount),
            "LiquidityPool: payment transfer failed"
        );
        
        emit FallbackPaymentMade(_user, _merchantAddress, _amount, address(this));
    }
    
    function _executeMultiPoolPayment(address _user, address _merchantAddress, uint256 _amount) internal {
        address[] memory allPools = IPoolRegistry(factoryOwner).getAllPools();
        uint256 remainingAmount = _amount;
        uint256 poolsUsed = 0;
        
        // First, use available liquidity from this pool
        uint256 availableHere = this.getAvailableLiquidity();
        if (availableHere > 0) {
            uint256 amountFromHere = remainingAmount > availableHere ? availableHere : remainingAmount;
            remainingAmount -= amountFromHere;
        }
        
        // If more liquidity needed, borrow from other pools
        if (remainingAmount > 0) {
            for (uint256 i = 0; i < allPools.length && remainingAmount > 0; i++) {
                if (allPools[i] != address(this) && allPools[i] != address(0)) {
                    LiquidityPool otherPool = LiquidityPool(allPools[i]);
                    uint256 otherPoolLiquidity = otherPool.getAvailableLiquidity();
                    
                    if (otherPoolLiquidity > 0) {
                        uint256 amountToBorrow = remainingAmount > otherPoolLiquidity ? otherPoolLiquidity : remainingAmount;
                        
                        // Borrow from other pool
                        otherPool.lendToPool(address(this), amountToBorrow);
                        
                        // Fixed: Update total inter-pool loan tracking
                        totalInterPoolLoans += amountToBorrow;
                        interPoolLoansByPool[allPools[i]] += amountToBorrow;
                        
                        remainingAmount -= amountToBorrow;
                        poolsUsed++;
                        
                        emit InterPoolLoanMade(allPools[i], address(this), amountToBorrow);
                    }
                }
            }
        }
        
        require(remainingAmount == 0, "LiquidityPool: insufficient liquidity across all pools");
        
        // Record debt in primary pool (this pool)
        userDebts[_user].push(Debt({
            user: _user,
            merchantAddress: _merchantAddress,
            amount: _amount,
            timestamp: block.timestamp,
            isRepaid: false
        }));
        
        // Set unstake timelock
        uint256 newTimelock = block.timestamp + UNSTAKE_TIME_LOCK_DURATION;
        if (newTimelock > debtRelatedUnstakeTimelock[_user]) {
            debtRelatedUnstakeTimelock[_user] = newTimelock;
        }
        
        // Transfer funds to merchant
        require(
            stakingToken.transfer(_merchantAddress, _amount),
            "LiquidityPool: payment transfer failed"
        );
        
        emit MultiPoolFallbackPayment(_user, _merchantAddress, _amount, poolsUsed + 1);
    }
    
    // New function for inter-pool lending
    function lendToPool(address _borrowerPool, uint256 _amount) external onlyRegisteredPool whenActive {
        require(_amount > 0, "LiquidityPool: amount must be greater than 0");
        require(this.getAvailableLiquidity() >= _amount, "LiquidityPool: insufficient liquidity to lend");
        
        // Fixed: Update total inter-pool lending tracking
        totalInterPoolLent += _amount;
        interPoolLentToPool[_borrowerPool] += _amount;
        
        // Transfer tokens to borrower pool
        require(
            stakingToken.transfer(_borrowerPool, _amount),
            "LiquidityPool: lending transfer failed"
        );
    }
    
    // New function for repaying inter-pool loans
    function repayInterPoolLoan(address _lenderPool, uint256 _amount) external onlyFactoryOwner {
        require(_amount > 0, "LiquidityPool: amount must be greater than 0");
        require(totalInterPoolLoans >= _amount, "LiquidityPool: repayment exceeds loan amount");
        require(interPoolLoansByPool[_lenderPool] >= _amount, "LiquidityPool: repayment exceeds loan from this pool");
        
        // Transfer repayment to lender pool
        require(
            stakingToken.transfer(_lenderPool, _amount),
            "LiquidityPool: repayment transfer failed"
        );
        
        // Fixed: Update loan tracking
        totalInterPoolLoans -= _amount;
        interPoolLoansByPool[_lenderPool] -= _amount;
        LiquidityPool(_lenderPool).receiveInterPoolRepayment(_amount);
        
        emit InterPoolLoanRepaid(address(this), _lenderPool, _amount);
    }
    
    // New function to receive inter-pool loan repayments
    function receiveInterPoolRepayment(uint256 _amount) external onlyRegisteredPool {
        require(totalInterPoolLent >= _amount, "LiquidityPool: repayment exceeds lent amount");
        
        // Fixed: Update total lending tracking
        totalInterPoolLent -= _amount;
        interPoolLentToPool[msg.sender] -= _amount;
    }
    
    function repayDebt(uint256 _debtIndex, uint256 _amount) external whenActive {
        require(_amount > 0, "LiquidityPool: amount must be greater than 0");
        require(_debtIndex < userDebts[msg.sender].length, "LiquidityPool: invalid debt index");
        
        Debt storage debt = userDebts[msg.sender][_debtIndex];
        require(!debt.isRepaid, "LiquidityPool: debt already repaid");
        require(_amount <= debt.amount, "LiquidityPool: amount exceeds debt");
        
        // Transfer repayment from user to pool
        require(
            stakingToken.transferFrom(msg.sender, address(this), _amount),
            "LiquidityPool: repayment transfer failed"
        );
        
        // Update debt
        if (_amount == debt.amount) {
            debt.isRepaid = true;
        } else {
            debt.amount -= _amount;
        }
        
        // Check if user has no active debts to clear timelock
        if (this.getActiveDebtAmount(msg.sender) == 0) {
            debtRelatedUnstakeTimelock[msg.sender] = 0;
        }
        
        emit DebtRepaid(msg.sender, _amount);
    }
    
    function distributeRewards() external onlyFactoryOwner whenActive {
        require(rewardsPot > 0, "LiquidityPool: no rewards to distribute");
        require(lpToken.totalSupply() > 0, "LiquidityPool: no LP tokens to distribute to");
        
        uint256 rewardsToDistribute = rewardsPot;
        rewardsPot = 0;
        
        // For gas efficiency, we'll use a pull-based reward system
        // This function just marks rewards as available for claiming
        
        emit RewardsDistributed(rewardsToDistribute, block.timestamp);
    }
    
    // Emergency functions
    function emergencyWithdraw() external onlyFactoryOwner {
        require(status == PoolStatusContract.INACTIVE, "LiquidityPool: pool must be inactive");
        
        uint256 balance = stakingToken.balanceOf(address(this));
        if (balance > 0) {
            stakingToken.transfer(factoryOwner, balance);
        }
    }
}

// Interface for Pool Registry (to be implemented by factory)
interface IPoolRegistry {
    function getAllPools() external view returns (address[] memory);
    function isRegisteredPool(address pool) external view returns (bool);
}
